import React from "react";

const Article = ({ article }) => {
    const { title, pubDate, link, author, description, categories, enclosure } = article;
    return (
        <div className="m-8 p-5 border border-gray-400 hover:border-blue-500">
            <a
                target="_blank"
                href={link}
                className="text-left no-underline text-gray-800"
            >
                {/* <img src={enclosure.link} alt={title} /> */}
                <p className="p-0 my-2 text-m text-gray-900 border border-gray-700 rounded">{categories[0]}</p>
                <h3 className="m-0 text-black-500 text-lg font-bold">
                    {title}
                </h3>
                <p className="m-0 pt-3 py-0 pb-0 text-sm text-gray-900">
                    {description}
                </p>

                <ul className="flex justify-between my-2 pt-2">
                    <li className="flex">
                        <p className="text-m text-red-700">{author}</p>
                    </li>

                    <li className="flex">
                        <p className="text-m text-gray-900">{new Date(pubDate).toDateString()}</p>
                    </li>
                </ul>


            </a>
        </div>
    );
}

export default Article;
