import React from "react";

const Article = ({ article }) => {
    const { title, pubDate, link, author, description, categories, enclosure } = article;
    return (
        <div className="m-8 p-5 border border-gray-400 hover:border-blue-500">
            <a
                target="_blank"
                href={link}
                className="text-left no-underline text-gray-800"
            >
                {/* <img src={enclosure.link} alt={title} /> */}
                <p>{categories[0]}</p>
                <h3 className="m-0 text-black-500 text-lg font-bold">
                    {title}
                </h3>
                <p className="m-0 pt-3 py-0 pb-0 text-sm text-gray-900">
                    {description}
                </p>

                <ul className="flex justify-between px-4 my-4 py-1">
                    <li className="flex px-2 py-1">
                        <a target="_blank" href={link} className="text-blue-500	no-underline text-sm">
                            <p>{title}</p>
                            <p>{description}</p>
                        </a>
                    </li>

                    <li className="flex px-2 py-1">
                        <a target="_blank" href="https://github.com/imboss712" className="text-blue-500	no-underline text-sm">Code</a>
                    </li>
                </ul>
                <p>
                    {author}
                </p>
                <p>{new Date(pubDate).toDateString()}</p>
            </a>
        </div>
    );
}

export default Article;
